snippet:
chocolatey_source '${1:name}' do
  source_name '${2:source_name}'
  source '${3:source}'
  priority ${4:2}
  action :${5:add}
end

dsc_script '${1:name}' do
  code <<-EOH
  ${2:}
  EOH
  action :${3:run}
end

group '${1:www-data}' do
  members ['${2:maintenance}', '${3:clicron}']
  append ${4:false}
  action :${5:create}
end

http_request '${1:some_message}' do
  url '${2:http://example.com/check_in}'
  message ${3:{:some => 'data'}.to_json}
  action :${5:post}
end

ksh '${1:name}' do
  code <<-EOH
  ${2:code}
  EOH
  action :${3:run}
end

macos_userdefaults '${1:name}' do
  domain '${2:AppleKeyboardUIMode}'
  global ${3:true}
  value '${4:2}'
  action :${5:write}
end

powershell_script '${1:read-env-var}' do
  cwd ${2:C:\\temp}
  code <<-EOH 
  ${3:$stream = [System.IO.StreamWriter] "./test-read-env-var.txt"
  $stream.WriteLine("FOO is $env:foo")
  $stream.close()}
  EOH
  action :${3:run}
end

reboot '${1:now}' do
  action :${2:nothing}
  reason '${3:Need to reboot when the run completes successfully.}'
  delay_mins ${4:5}
end

ruby '${1:desc}' do
  cwd '${2:/tmp}'
  code <<-EOH
    ${3:mkdir -p #{extract_path\}
    tar xzf #{src_filename\} -C #{extract_path\}
    mv #{extract_path\}/*/* #{extract_path\}/}
    EOH
  action :${4:run}
end

ruby_block '${1:desc}' do
  block do
    ${2:Chef::Config.from_file("/etc/chef/client.rb")}
  end
  action :${3:run}
end

sudo '${1:passwordless-access}' do
  commands %{2:['systemctl restart httpd', 'systemctl restart mysql']}
  nopasswd false
  action :${3:create}
end

windows_ad_join '${1:ad.example.org}' do
  domain_user ${2:'nick'}
  password '${3:p@ssw0rd1}'
  action :${4:join}
end

windows_feature_dism '${1:name}' do
  all ${2:false}
  source '${3:localrepository}'
  action :${4:install}
end

windows_feature_powershell '${1:name}' do
  all ${2:false}
  source '${3:localrepository}'
  action :${4:install}
end

windows_task '${1:chef-client}' do
  command '${2:chef-client}'
  run_level :${3:highest}
  frequency '${4:weekly}'
  frequency_modifier ${5:2}
  day ${6:Mon, Fri}
  action :${7:create}
end
